body.script:
var elem = document.getElementById("qrcode");
var qrcode = new QRCode(elem, {
    width : 100,
    height : 100
});

function makeCode () {
    var elText = document.getElementById("text");

    if (elText.value === "") {
        //alert("Input a text");
        //elText.focus();
        return;
    }

    qrcode.makeCode(elText.value);
}

makeCode();

document.getElementById("text").onkeyup = function (e) {
        makeCode();
};
